$(window).on('message', function(e){ // onclick
  for (var i = 0; i <= 30 ; i++){ // Reping 30 fois toute la liste (semblant de non-stop)
    $("#table_agencies > tbody > tr").each(function(){
      $.ajax({ // demarage ajax
        url: '/agencies/do_ping', // url du controller rails pour pinger (methode doPing)
        type: 'GET',
        dataType: 'script',
        data: {
          host: e.originalEvent.data[0] // transfert de l'addressip dans params[:host]
        },
        success: function(result){
          postMessage(result);
        }
      });
    });
  }
});
(function() {
  $(function() {
    return $(document).on('change', '#procedure_category_id', function(evt) {
      return $.ajax('/procedures/:id/update_subcats', {
        type: 'GET',
        dataType: 'script',
        data: {
          category_id: $('#procedure_category_id option:selected').val()
        },
        error: function(jqXHR, textStatus, errorThrown) {
          return console.log("AJAAX Error: " + textStatus + ", " + jqXHR + ", " + errorThrown);
        },
        success: function(data, textStatus, jqXHR) {
          return console.log("Dynamic category select OK!");
        }
      });
    });
  });

}).call(this);
